import logo from './logo.svg';
import './App.css';
import {MapContainer, Marker, Popup, TileLayer} from "react-leaflet";
import {icon} from "leaflet";
import React, { Component } from "react";

var cordinaties;

var options = {
  enableHighAccuracy: true,
  timeout: 5000,
  maximumAge: 0,
};
function success(pos) {
  cordinaties = pos.coords;
  console.log("Your current position is:");
  console.log(`Latitude : ${cordinaties.latitude}`);
  console.log(`Longitude: ${cordinaties.longitude}`);
  console.log(`More or less ${cordinaties.accuracy} meters.`);
}

function errors(err) {
  console.warn(`ERROR(${err.code}): ${err.message}`);
}


export default function App(){

    if (navigator.geolocation) {
      navigator.permissions
          .query({ name: "geolocation" })
          .then(function (result) {
            if (result.state === "granted") {
              console.log(result.state);
              //If granted then you can directly call your function here

              navigator.geolocation.getCurrentPosition(success);
            } else if (result.state === "prompt") {
              navigator.geolocation.getCurrentPosition(success, errors, options);
            } else if (result.state === "denied") {
              //If denied then you have to show instructions to enable location
            }
            result.onchange = function () {
              console.log(result.state);
            };
          });
    } else {
      alert("Sorry Not available!");
    }

    return (
        <div>
          <MapContainer center={[cordinaties.latitude, cordinaties.longitude]} zoom={13} scrollWheelZoom={false}>
            <TileLayer
                attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            <Marker position={[51.505, -0.09]}>
              <Popup>
                A pretty CSS3 popup. <br /> Easily customizable.
              </Popup>
            </Marker>
          </MapContainer>
        </div>
    );

}

